rules.addRule( "root", new BeanPropertySetterRule( "alpha" ) );
rules.addRule( "root/alpha", new BeanPropertySetterRule( "beta" ) );
rules.addRule( "root/delta", new BeanPropertySetterRule( "delta" ) );

